mapper.select:
select id as id, ifnull(role_id, null) as roleId, ifnull(username, "") as username, ifnull(email, "") as email, ifnull(qq, null) as qq, ifnull(wechat, null) as wechat, ifnull(mobile, null) as mobile, ifnull(sex, null) as sex, ifnull(intro, "") as intro, ifnull(status, null) as status, ifnull(create_time, null) as createTime, ifnull(last_update_time, null) as lastUpdateTime, ifnull(last_update_by, null) as lastUpdateBy from `user` where is_delete = 0
and (username like CONCAT('%', #{keyword}, '%') or email like CONCAT('%', #{keyword}, '%'))
and status = #{status}
and id = #{id}
order by role_id asc, create_time desc limit #{startIndex}, #{pageSize}

select count(1) from `user` where is_delete = 0
and (username like CONCAT('%', #{keyword}, '%') or email like CONCAT('%', #{keyword}, '%'))
and status = #{status}
and id = #{id}
